declare @compLevel smallint select @compLevel = {0}
select so.*, CASE WHEN @compLevel < 90 THEN USER_NAME(so.uid) ELSE SCHEMA_NAME(so.uid) END 'owner',
CASE WHEN @compLevel < 90 THEN CASE WHEN so.uid IS NULL THEN (so.name) ELSE (USER_NAME(so.uid)) + '.' + (so.name) END ELSE CASE WHEN so.uid IS NULL THEN (so.name) ELSE (SCHEMA_NAME(so.uid)) + '.' + (so.name) END END 'FullName' from sysobjects so where ( so.xtype = 'IF' ) AND (category = 0) order by CASE WHEN @compLevel < 90 THEN USER_NAME(so.uid) ELSE SCHEMA_NAME(so.uid) END, so.name

declare @compLevel smallint select @compLevel = {0}
select so.*, CASE WHEN @compLevel < 90 THEN USER_NAME(so.uid) ELSE SCHEMA_NAME(so.uid) END 'owner',
CASE WHEN @compLevel < 90 THEN CASE WHEN so.uid IS NULL THEN (so.name) ELSE (USER_NAME(so.uid)) + '.' + (so.name) END ELSE CASE WHEN so.uid IS NULL THEN (so.name) ELSE (SCHEMA_NAME(so.uid)) + '.' + (so.name) END END 'FullName' from sysobjects so where ( (so.xtype = 'TF') OR (so.xtype = 'IF') ) AND (category = 0) order by CASE WHEN @compLevel < 90 THEN USER_NAME(so.uid) ELSE SCHEMA_NAME(so.uid) END, so.name

